import styled, {css} from 'styled-components';


const shrinkLabelStyles = css` 
  top: -14px;
  font-size: 12px;
  color: black;
`;

const formInputLabel = css` 
  top: -14px;
  font-size: 12px;
  color: black;
`;

const getFormStyles = props =>  {
  if(props.shrink) {
    return shrinkLabelStyles;
  }

  return props.formInputLabel ? formInputLabel : '';
}
export const GroupContainer = styled.div`
    position: relative;
    margin: 45px 0;

    input[type='password'] {
      letter-spacing: 0.3em;
    }

    ${getFormStyles}

    &.shrink {
      ${shrinkLabelStyles};
    }
`;
export const FormInputContainer = styled.input`
    background: none;
    background-color: white;
    color: grey;
    font-size: 18px;
    padding: 10px 10px 10px 5px;
    display: block;
    width: 100%;
    border: none;
    border-radius: 0;
    border-bottom: 1px solid grey;
    margin: 25px 0;

    &:focus {
      outline: none;
    }

    &:focus ~ .form-input-label {
      ${shrinkLabelStyles};
    }
`;

export const LabelContainer = styled.label`

`;
